import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { useBooks } from "../../context/BookContext";

const initialForm = {
  title: "",
  author: "",
  status: "milik",
};

const BookForm = ({ selectedBook, onFinish }) => {
  const [form, setForm] = useState(initialForm);
  const { addBook, updateBook } = useBooks();

  useEffect(() => {
    if (selectedBook) setForm(selectedBook);
  }, [selectedBook]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.title || !form.author)
      return alert("Judul dan penulis wajib diisi");

    if (selectedBook) {
      updateBook(form);
    } else {
      addBook({ ...form, id: Date.now() });
    }

    setForm(initialForm);
    onFinish();
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="bg-[#f4e9d8] p-4 rounded-2xl shadow-md"
    >
      <h2 className="text-xl font-semibold text-[#5c3a21] mb-4">
        {selectedBook ? "Edit Buku" : "Tambah Buku"}
      </h2>
      <input
        type="text"
        name="title"
        placeholder="Judul Buku"
        value={form.title}
        onChange={handleChange}
        className="w-full p-2 mb-2 border rounded"
      />
      <input
        type="text"
        name="author"
        placeholder="Penulis"
        value={form.author}
        onChange={handleChange}
        className="w-full p-2 mb-2 border rounded"
      />
      <select
        name="status"
        value={form.status}
        onChange={handleChange}
        className="w-full p-2 mb-4 border rounded"
      >
        <option value="milik">Dimiliki</option>
        <option value="baca">Sedang Dibaca</option>
        <option value="beli">Ingin Dibeli</option>
      </select>
      <button
        type="submit"
        className="bg-[#5c3a21] text-white px-4 py-2 rounded"
      >
        {selectedBook ? "Update" : "Tambah"}
      </button>
    </form>
  );
};

BookForm.propTypes = {
  selectedBook: PropTypes.object,
  onFinish: PropTypes.func.isRequired,
};

export default BookForm;
