import useBookStats from "../../hooks/useBookStats";
import { Link } from "react-router-dom";

const Stats = () => {
  const { total, milik, baca, beli } = useBookStats();

  return (
    <div className="max-w-xl mx-auto p-6 text-[#5c3a21]">
      <h1 className="text-3xl font-bold mb-4 text-center">Statistik Buku</h1>
      <div className="bg-[#f4e9d8] p-4 rounded-2xl shadow space-y-3">
        <p className="text-lg">
          Total Buku: <span className="font-semibold">{total}</span>
        </p>
        <p>
          Buku Dimiliki: <span className="font-semibold">{milik}</span>
        </p>
        <p>
          Sedang Dibaca: <span className="font-semibold">{baca}</span>
        </p>
        <p>
          Ingin Dibeli: <span className="font-semibold">{beli}</span>
        </p>
      </div>
      <div className="text-center mt-6">
        <Link
          to="/"
          className="text-sm text-white bg-[#5c3a21] px-4 py-2 rounded"
        >
          Kembali ke Beranda
        </Link>
      </div>
    </div>
  );
};

export default Stats;
