import { useState } from "react";
import { useBooks } from "../../context/BookContext";
import BookForm from "../BookForm/BookForm";

const BookList = () => {
  const { books, deleteBook, searchTerm, filterStatus } = useBooks();
  const [selectedBook, setSelectedBook] = useState(null);

  const filteredBooks = books.filter((book) => {
    const matchSearch = book.title
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
    const matchFilter = filterStatus === "all" || book.status === filterStatus;
    return matchSearch && matchFilter;
  });

  return (
    <div className="space-y-4">
      <BookForm
        selectedBook={selectedBook}
        onFinish={() => setSelectedBook(null)}
      />

      <div className="grid gap-4">
        {filteredBooks.map((book) => (
          <div
            key={book.id}
            className="bg-[#f4e9d8] p-4 rounded-2xl shadow flex justify-between items-center"
          >
            <div>
              <h3 className="text-lg font-semibold text-[#5c3a21]">
                {book.title}
              </h3>
              <p className="text-sm text-[#7b5e47]">{book.author}</p>
              <span className="text-xs text-[#9c7c63]">
                Status: {book.status}
              </span>
            </div>
            <div className="space-x-2">
              <button
                onClick={() => setSelectedBook(book)}
                className="px-3 py-1 bg-yellow-600 text-white rounded"
              >
                Edit
              </button>
              <button
                onClick={() => deleteBook(book.id)}
                className="px-3 py-1 bg-red-700 text-white rounded"
              >
                Hapus
              </button>
            </div>
          </div>
        ))}
        {filteredBooks.length === 0 && (
          <p className="text-center text-[#5c3a21]">
            Tidak ada buku ditemukan.
          </p>
        )}
      </div>
    </div>
  );
};

export default BookList;
