import { createContext, useContext, useState, useEffect } from "react";
import PropTypes from "prop-types";
import useLocalStorage from "../hooks/useLocalStorage";

const BookContext = createContext();

export const BookProvider = ({ children }) => {
  const [storedBooks, setStoredBooks] = useLocalStorage("books", []);
  const [books, setBooks] = useState(storedBooks);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterStatus, setFilterStatus] = useState("all");

  useEffect(() => {
    setStoredBooks(books);
  }, [books, setStoredBooks]);

  const addBook = (book) => setBooks([...books, book]);
  const updateBook = (updatedBook) =>
    setBooks(
      books.map((book) => (book.id === updatedBook.id ? updatedBook : book))
    );
  const deleteBook = (id) => setBooks(books.filter((book) => book.id !== id));

  return (
    <BookContext.Provider
      value={{
        books,
        addBook,
        updateBook,
        deleteBook,
        searchTerm,
        setSearchTerm,
        filterStatus,
        setFilterStatus,
      }}
    >
      {children}
    </BookContext.Provider>
  );
};

BookProvider.propTypes = {
  children: PropTypes.node.isRequired,
};

export const useBooks = () => useContext(BookContext);
